import { AiOutlineMinus, AiOutlinePlus } from 'react-icons/ai';
const Question = ({index,title,info}) => {
  const [passInfo, setPassInfo] = useState(false);
  const toggle=(i)=>{
    if(passInfo===i){
      return setPassInfo(i);
    }
    setPassInfo("");

  }

  return (
  <article className='question'>
    <header>
      <h4>{title}</h4>
      <p className='btn' onClick={() => setpassInfo(!isActive)}>
        {passInfo ? <AiOutlineMinus /> : <AiOutlinePlus />}
      </p>
    </header>
   {passInfo && <p>{info}</p>}
  </article>
    );
};

export default Question;
