  const [questions,setQuestions] = useState(data);

  return (
    <main>
    <div className='container'>
      <h3>question and answer</h3>
      <section className='info'>
        {questions.map((question,i)=>
          <Question key={question.id} index={question.id} />
        )}
      </section>
    </div>
  </main>

    );
}

export default App;
